
public class Calculadora {

	public static int somar(int i, int j) {
		// TODO Auto-generated method stub
		return i+j ;
	}
	
	public static int mult(int i, int j) {
		// TODO Auto-generated method stub
		return i*j ;
	}	

	public static int divi(int i, int j) {
		// TODO Auto-generated method stub
		return i/j ;
	}	

	public static int subi(int i, int j) {
		// TODO Auto-generated method stub
		return i-j ;
	}	
}
